    private void MateriaCursoBotonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_MateriaCursoBotonActionPerformed
        // TODO add your handling code here:
        ComprarLectorBoton.setEnabled(false);
        ComprarBoton.setEnabled(true);
        ConsultarBoton.setEnabled(true);
        int curso=1;
        if (PrimeroCajita.isSelected()){
        curso=1;
        }else if (SegundoCajita.isSelected()){
        curso=2;
        }else if (TerceroCajita.isSelected()){
        curso=3;
        }else if (CuartoCajita.isSelected()){
        curso=4;
        }else if (QuintoCajita.isSelected()){
        curso=5;
        }else if (SextoCajita.isSelected()){
        curso=6;
        }
        
        if (MateriaCaja.getText().equals("") ) {
            ResultadoBusqueda.setText("Por favor, introduzca los parámetros correctamente.");

        } else {
            
            ResultadoBusqueda.setText(this.parent.getGestor().busquedaMateriaCurso(MateriaCaja.getText(), curso));
        }

    }//GEN-LAST:event_MateriaCursoBotonActionPerformed

