    private void PrecioBuscarBotonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_PrecioBuscarBotonActionPerformed
        // TODO add your handling code here:
        ComprarLectorBoton.setEnabled(true);
        ComprarBoton.setEnabled(false);
        ConsultarBoton.setEnabled(false);
        int i = PrecioSlider.getValue();
        double d = (double) i;
        Lector[] nuevo = this.parent.getGestor().busquedaAvPrecio(d);
       // for (int j = 0; j < nuevo.length; j++) {               
        //     ResultadoBusqueda.setText(nuevo[j].toStringA());

        // }
        int j = 0;
        while (j < nuevo.length) {
            ResultadoBusqueda.setText(nuevo[j].toStringA());
            j++;
        }
    }//GEN-LAST:event_PrecioBuscarBotonActionPerformed

