    private void ComprarBotonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ComprarBotonActionPerformed
        // TODO add your handling code here:
        String Isbn = IsbnCompraCaja.getText();
        if (Isbn.equals("")) {

            ResultadoBusqueda.setText("Por favor, introduzca los parámetros correctamente.");

        } else {

            if (this.parent.getGestor().containsTexto(Isbn) || this.parent.getGestor().containsLectura(Isbn) || this.parent.getGestor().containsDicc(Isbn)) {

                String Isbn2 = IsbnCompraCaja.getText();
                this.parent.getGestor().ComprarLibro(Isbn2);
                ComprarPublicacionDigital compra = new ComprarPublicacionDigital(this.parent);
                this.setVisible(false);
                compra.setVisible(true);

            } else {
                ResultadoBusqueda.setText("No se han encontrado coincidencias");
            }
        }
    }//GEN-LAST:event_ComprarBotonActionPerformed

    private void ConsultarBotonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ConsultarBotonActionPerformed
        // TODO add your handling code here:
         
        String Isbn = IsbnCompraCaja.getText();
        if (Isbn.equals("")) {

            ResultadoBusqueda.setText("Por favor, introduzca los parámetros correctamente.");

        } else {

            if (this.parent.getGestor().containsTexto(Isbn) || this.parent.getGestor().containsLectura(Isbn) || this.parent.getGestor().containsDicc(Isbn)) {

                
                this.parent.getGestor().ComprarLibro(Isbn);
                ComprarPublicacionDigitalPorConsulta consulta = new ComprarPublicacionDigitalPorConsulta(this.parent);
                this.setVisible(false);
                consulta.setVisible(true);

            } else {
                ResultadoBusqueda.setText("No se han encontrado coincidencias");
            }
        }


    }//GEN-LAST:event_ConsultarBotonActionPerformed

    private void ComprarLectorBotonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ComprarLectorBotonActionPerformed
        // TODO add your handling code here:
        String numero = NumSerieCompra.getText();
        if (numero.equals("")) {

            ResultadoBusqueda.setText("Por favor, introduzca los parámetros correctamente.");

        } else {

            if (this.parent.getGestor().containsLector(numero)) {

                String lector = NumSerieCompra.getText();
                this.parent.getGestor().ComprarLector(lector);

                ComprarLector compra = new ComprarLector(this.parent);
                this.setVisible(false);
                compra.setVisible(true);

            } else {
                ResultadoBusqueda.setText("No se han encontrado coincidencias");
            }
        }
    }//GEN-LAST:event_ComprarLectorBotonActionPerformed

